mapper.select:
SELECT job_record_resource_mapping.resource_id, job_record_resource_mapping.resource_type, job_record_resource_mapping.create_time, job_record_resource_mapping.job_record_id, job_record.type, job_record.`status`, job_record.description, job_record.params, job_record.result FROM job_record_resource_mapping job_record_resource_mapping RIGHT JOIN ( SELECT MAX(create_time) AS create_time, resource_type AS resource_type, resource_id AS resource_id FROM job_record_resource_mapping job_record_resource_mapping WHERE job_record_resource_mapping.resource_id IN
#{id}
AND job_record_resource_mapping.job_type IN
#{jobType}
GROUP BY job_record_resource_mapping.resource_id, job_record_resource_mapping.resource_type ) t1 ON t1.resource_id = job_record_resource_mapping.resource_id AND t1.resource_type = job_record_resource_mapping.resource_type AND t1.create_time = job_record_resource_mapping.create_time LEFT JOIN job_record job_record ON job_record.id=job_record_resource_mapping.job_record_id WHERE job_record_resource_mapping.resource_type is not null
